  load("mnist_all.mat");  trainMistakes = zeros(1000,1);  testMistakes = zeros(1000,1);  for m=1:1000  fprintf('%i\n', m)  [Xtrain,Ytrain,Xtest,Ytest] = gensmallm(train3, train5, test3, test5, m);  w = perceptron(m, length(Xtrain(1,:)), Xtrain, Ytrain, 20*m);  h = rule(w);  trainMistakes(m) = errorRate(h,Xtrain,Ytrain);  testMistakes(m) = errorRate(h,Xtest,Ytest);  fprintf('%i\n', trainMistakes(m))  fprintf('%i\n', testMistakes(m))  fflush(stdout);  end  