load("ex2data.mat")lambdas = [0.01,0.1,1];sigmas = [0.05,1,2];m=length(Xtrain);d=length(Xtrain(1,:));result = zeros(length(lambdas),length(sigmas));for l=1:length(lambdas)    for s=1:length(sigmas)          a = softsvmrbf(lambdas(l),sigmas(s),m,d,Xtrain,Ytrain);            kMatrix =@(x) exp(-vecnorm((Xtrain - x)').^2./(2*sigmas(s)));          h=@(alpha) @(x) sign(kMatrix(x)*alpha);          result(l,s) = errorRate(h,a,Xtrain,Ytrain);     endend  